    public function testStore()
    {
        $user = FactoryMuffin::create('FireflyIII\User');
        $data = [
            'name'        => 'Something',
            'match'       => 'Something',
            'amount_min'  => 100,
            'user'        => $user->id,
            'amount_max'  => 110,
            'date'        => new Carbon,
            'repeat_freq' => 'monthly',
            'skip'        => 0,
            'automatch'   => 1,
            'active'      => 1,

        ];
        $bill = $this->object->store($data);

        $this->assertEquals($data['name'], $bill->name);
    }

    /**
