    public function testUpdate()
    {
        $bill    = FactoryMuffin::create('FireflyIII\Models\Bill');
        $data    = [
            'name'        => 'new Name',
            'match'       => $bill->match,
            'amount_min'  => 100,
            'amount_max'  => 110,
            'date'        => new Carbon,
            'repeat_freq' => 'monthly',
            'skip'        => 0,
            'automatch'   => 1,
            'active'      => 1,

        ];
        $newBill = $this->object->update($bill, $data);

        $this->assertEquals($data['name'], $newBill->name);
